sys_script_include.script:
var NeedItUtils = Class.create();

NeedItUtils.prototype = {
    initialize: function() {},

    isDatePast: function(dateToCheck) {

		// Get the current time for the reference.
        var currentTime = new GlideDateTime();
		// Convert the passed date into the GlideDateTime object.
        var _dateToCheck = new GlideDateTime(dateToCheck);

		// Check if the _dateToCheck is older than the current time.
		// If if is then return true, else return false.
        if (_dateToCheck.before(currentTime)) {
            return true;
        } else {
            return false;
        }
    },

	isDateToday: function(dateToCheck) {
		// Get the current time in local for the reference.
		var todaysDate = new GlideDateTime().getLocalDate();
		var _dateToCheck = new GlideDateTime(dateToCheck).getLocalDate();

		if (todaysDate.compareTo(_dateToCheck) == 0) {
			return true;
		} else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
